import '../css/page6.css';
import React from 'react'
import anime from "animejs";

function Page6(props){
  let obj1 = document.querySelector('#Page6Point1Text');
  if(obj1 !== null) obj1.innerHTML = 0;
  let obj2 = document.querySelector('#Page6Point3');
  if(obj2 !== null) obj2.style.display = '';
  function animation(){
    anime.timeline().add({
      targets: '#Page6Point1',
      opacity: 1,
      translateX: 150,
    }).add({
      targets: '#Page6Point2',
      opacity: 1,
      translateX: -150,
    }).add({
      targets: '#Page6Point3',
      opacity: 1,
      translateX: -95,
      translateY: 25,
    }).add({
      targets: '#Page6Point3',
      translateX: 180,
      easing: "linear",
      duration: 1500,
      complete: function(){
        // let obj1 = document.querySelector('#Page6Point3');
        // obj1.style.display = 'none';
        // anime({
        //   targets: '#Page6Point3',
        //   opacity: 0,
        //   translateX: -265,
        // });
        let obj2 = document.querySelector('#Page6Point1Text');
        obj2.innerHTML = 8;
      }
    }, '-=600');
  }
  if(props.display) animation();

  return(
      <div style={{display: props.display?'': 'none'}}>
        <div id="Page6Point1"> <span id="Page6Point1Text"> 0 </span> </div>
        <div id="Page6Point2"> <span id="Page6Point2Text"> 8 </span> </div>
        <div id="Page6Point3"/>
        <div id="Page6Text1"> We also have a <span id="Yellow"> client </span> that can send value to the <span id="Blue">node</span> </div>
      </div>
  );
}

export default Page6;
