import '../css/page2.css';
import React, {Component, useState} from 'react'
import anime from "animejs";

function Page2(props){
  const [flag, setFlag] = useState(false);
  function animation(){
    anime({
      targets: '#Point',
      scale: 4,
      duration: 5000,
      easing: 'linear',
      complete: function () {
        console.log("rerendering");
        anime({
          targets: '#Point',
          scale: 1,
          duration: 1000,
          easing: 'linear'
        })
      }
    });
  }
    if(props.display){
      animation();
      setFlag(true);
    }
    else setFlag(false);

    return(
        <div style={{display: props.display?'': 'none'}}>
          <div id="Point"/>
          <div className="text" style={{display: flag?'': 'none'}}> This is a point </div>
        </div>
    );
}

export default Page2;
