import '../css/page200.css';
import React, { useEffect, useRef } from 'react';
import anime from "animejs";

function Page200(props) {
  const progressBarRef = useRef(null);

  useEffect(() => {
    const progressBar = progressBarRef.current;
    const initialProgress = 0; // 初始进度
    const finalProgress = 251.5; // 最终进度

    const animation = anime({
      targets: progressBar,
      strokeDasharray: [`${initialProgress} 1000`, `${finalProgress} 1000`], // 进度动画
      easing: 'linear',
      duration: 2000,
      autoplay: true,
    });

    return () => {
      animation.pause(); // 在组件卸载时停止动画
    };
  }, []);

  return (
      <svg className="progress-bar" viewBox="0 0 100 100">
        <circle
            className="progress-bar-background"
            cx="50"
            cy="50"
            r="40"
            stroke="#ddd"
            strokeWidth="10"
            fill="transparent"
        />
        <circle
            ref={progressBarRef}
            className="progress-bar-indicator"
            cx="50"
            cy="50"
            r="40"
            stroke="#ff0000"
            strokeWidth="10"
            fill="transparent"
            strokeDasharray="0 251.2"
            strokeDashoffset="0"
        />
      </svg>
  );
};
export default Page200;
