import '../css/page6.css';
import React from 'react'
import anime from "animejs";

function Page6(props){
  function animation(){
    anime.timeline().add({
      targets: '#Page6Point1',
      scale: 0.7,
      translateX: 170,
    }).add({
      targets: '#Page6Point2',
      scale: 0.7,
      translateX: -150,
    }, '-=600').add({
      targets: '#Page6Point3',
      scale: 0.3,
      opacity: 0,
    }).add({
      targets: '#Page6Point3',
      opacity: [0,1],
      translateX: [950, 2000],
      easing: "linear",
      complete: function(){
        let obj = document.querySelector('#Page6Point1');
        obj.innerHTML = 8;
      }
    }, '-=600');
  }
  if(props.display) animation();

  return(
      <div style={{display: props.display?'': 'none'}}>
        <div id="Page6Point1"> 0 </div>
        <div id="Page6Point2"> <span id="Page6Point2Text"> 8 </span> </div>
        <div id="Page6Point3"/>

        <div id="Page6Text1"> We also have a <span id="Yellow"> client </span> that can send value to the <span id="Blue">node</span> </div>
      </div>
  );
}

export default Page6;
