import './App.css';
import React, {useEffect, useRef, useState} from "react";
import Page0 from "./pages/page0";
import Page2 from "./pages/page2";
import anime from "animejs";
import Page1 from "./pages/page1";



function App() {
  const [count, setCount] = useState(0);
  function clickBack(){
    setCount(count-1);
  }

  function clickNext(){
    setCount(count+1);
  }

  function animation(){
    let animation1 = anime({
      targets: '#Text',
      direction: 'alternate',
      loop: true,
      delay: 100,
      endDelay: 100,
      easing: 'easeOutExpo',
    });
  }
animation();

  return (

      <div className="App">
        <div className="content">
          click this {count}
        </div>
        <div id="Text"> hello world </div>
        <Page0 display = {count === 0}/>
        <Page1 display = {count === 1}/>
        <Page2 display = {count === 2}/>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
