import '../css/page20.css';
import React, { useEffect, useRef } from 'react';
import anime from "animejs";

function Page20(props) {
// 获取圆形div和进度条元素
  function animation(){
    let circle = document.getElementById('circle');
    let progress = document.getElementById('progress');

  // 设置动画属性
    anime({
      targets: progress,
      strokeDasharray: [0, 2 * Math.PI * circle.offsetWidth],
      strokeDashoffset: [anime.setDashoffset, 0],
      easing: 'linear',
      duration: 2000,
      loop: true
    });

  // 启动动画并设置进度
    animation.play();
    setProgress(0.45);

  // 调节进度的函数
    function setProgress(value) {
      // 将进度值限制在0到1之间
      let progressValue = Math.min(Math.max(value, 0), 1);

      // 计算进度条的偏移量
      let offset = anime.setDashoffset(progress);
      let targetOffset = anime.setDashoffset(progress) * (1 - progressValue);

      // 更新进度条动画
      animation.seek(0);
      animation.reset();
      animation.changeBegin = function () {
        progress.style.strokeDashoffset = offset;
      };
      animation.change = function () {
        progress.style.strokeDashoffset = progress.animatable.targets.innerHTML = offset
            - (offset - targetOffset) * animation.progress;
      };
      animation.play();
    }
  }
  if(props.display) animation();

  return (
      <div style={{display: props.display?'': 'none'}}>
        <div id="circle" className="circle">
          <div id="progress" className="progress"/>
        </div>
      </div>
  );
};

export default Page20;
