import './App.css';
import ReactAnime from 'react-animejs'
import React, {useEffect, useRef, useState} from "react";
import anime from "animejs";
const {Anime, stagger} = ReactAnime;



function App() {
  const [count, setCount] = useState(0);
  const [showPoint, setShowPoint] = useState(true);
  const tempToggle = useRef(null);

  const anime2 = useRef(null);
  const anime3 = useRef(null);

  function clickBack(){
    setCount(count-1);
    setShowPoint(!showPoint);
    tempToggle.current.style.display = !showPoint ? '' : 'none';
    animations(count+1);
  }

  function clickNext(){
    setCount(count+1);
    setShowPoint(!showPoint);
    tempToggle.current.style.display = !showPoint ? '' : 'none';
    animations(count+1);
  }

  function animations(curCount){
    if(curCount <= 0)
    switch(curCount){
      case 1:
        const anime1 = anime({
          targets: '#Point',
          scale: 5,
          duration: 5000,
          easing: 'linear'
        });

        break;
      case 2:
        break;
      case 3:
        break;
      default:
        break;
    }
  }
  console.log(showPoint);


  return (
      <div className="App">
        Hello World
        <div className="content" ref={tempToggle}>
          click this  {count}
        </div>
        <Anime className="animation"
            initial={[
              {
                targets: "#Box1",
                keyframes: [
                  {
                    translateX: 50
                  },
                  {
                    translateY: 50
                  },
                  {
                    translateX: 0
                  },
                  {
                    translateY: 0
                  }
                ],
                // easing:'spring',
                duration: 3500,
                loop: true
              }
            ]}
        >
          {/*<div id="Box1" />*/}
        </Anime>

        <Anime className="animation"
              initial={[
                {
                  targets: "#Box2",
                  keyframes: [
                    {
                      translateX: 150
                    },
                    {
                      translateY: 150
                    },
                    {
                      translateX: 100
                    },
                    {
                      translateY: 100
                    }
                  ],
                  // easing:'spring',
                  duration: 5000,
                  loop: true
                }
              ]}
                >
          <div id="Box1" style={{display: showPoint?'': 'none'}}/>
          <div id="Box2" style={{display: showPoint?'': 'none'}}/>
          <div id="Point" style={{textAlign: "center", position: "absolute", left: "48%", top: "45%", display: 'none'}}/>
        </Anime>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
