import './App.css';
import ReactAnime from 'react-animejs'
import React, {useEffect, useRef, useState} from "react";
import anime from "animejs";
const {Anime, stagger} = ReactAnime;



function App() {
  const [count, setCount] = useState(0);
  const [showPoint, setShowPoint] = useState(true);
  const [anime1, setAnime1] = useState(false);
  const tempToggle = useRef(null);
  const pointToggle = useRef(null);
  // let anime1 = React.createRef();
  // const anime2 = useRef(null);
  // const anime3 = useRef(null);
  function clickBack(){
    animations(count-1);
    setCount(count-1);
    setShowPoint(!showPoint);
    tempToggle.current.style.display = !showPoint ? '' : 'none';

  }

  function clickNext(){
    animations(count+1);
    setCount(count+1);
    setShowPoint(!showPoint);
    tempToggle.current.style.display = !showPoint ? '' : 'none';

  }

  function animations(curCount){
    console.log("what is the count here in animations " + curCount);
    setAnime1(false);
    switch(curCount){
      case 1:
        setAnime1(true);
        anime({
          targets: '#Point',
          scale: 4,
          duration: 5000,
          easing: 'linear',
          complete: function() {
            anime({
              targets: '#Point',
              scale: 1,
              duration: 10,
              easing: 'linear'
            })
          }
        });
        break;
      default:
        break;
    }
  }
  console.log(showPoint);


  return (
      <div className="App">
        <div className="content" ref={tempToggle}>
          click this  {count}
        </div>
        <Anime className="animation"
            initial={[
              {
                targets: "#Box1",
                keyframes: [
                  {
                    translateX: 50
                  },
                  {
                    translateY: 50
                  },
                  {
                    translateX: 0
                  },
                  {
                    translateY: 0
                  }
                ],
                // easing:'spring',
                duration: 3500,
                loop: true
              }
            ]}
        >
          {/*<div id="Box1" />*/}
        </Anime>

        <Anime className="animation"
              initial={[
                {
                  targets: "#Box2",
                  keyframes: [
                    {
                      translateX: 150
                    },
                    {
                      translateY: 150
                    },
                    {
                      translateX: 100
                    },
                    {
                      translateY: 100
                    }
                  ],
                  // easing:'spring',
                  duration: 5000,
                  loop: true
                }
              ]}
                >

          <div id="Box1" style={{display: showPoint?'': 'none'}}/>
          <div id="Box2" style={{display: showPoint?'': 'none'}}/>
        </Anime>
        <div id="Point" style={{position: "absolute", left: "50%", top: "50%", display: anime1?'': 'none'}}/>
        <div className="text-center" style={{position:"absolute", left: "50%", marginTop: "-50px", marginLeft: "-50px", bottom: "13%", color: "red", display: anime1?'': 'none'}}> This is a point </div>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
