import React, {Component} from 'react'
import ReactAnime from 'react-animejs'
import anime from "animejs";
const {Anime, stagger} = ReactAnime;

class page1 extends Component {
  constructor(props) {
    super(props);
    const flag = this.props;
    console.log(flag);
  }

  render(){
    return(
        <div style={{display: this.props?'': 'none'}}>
          <Anime className="animation"
                 initial={[
                   {
                     targets: "#Box1",
                     keyframes: [
                       {
                         translateX: 50
                       },
                       {
                         translateY: 50
                       },
                       {
                         translateX: 0
                       },
                       {
                         translateY: 0
                       }
                     ],
                     // easing:'spring',
                     duration: 3500,
                     loop: true
                   }
                 ]}
          >
            {/*<div id="Box1" />*/}
          </Anime>

          <Anime className="animation"
            initial={[
                {
                  targets: "#Box2",
                  keyframes: [
                    {
                      translateX: 150
                    },
                    {
                      translateY: 150
                    },
                    {
                      translateX: 100
                    },
                    {
                      translateY: 100
                    }
                  ],
                  // easing:'spring',
                  duration: 5000,
                  loop: true
                }
                ]}
          >
          <div id="Box1"/>
          <div id="Box2"/>
        </Anime>
    </div>
    );
  }
}

export default page1;
