import '../css/page2.css';
import React, {Component, useState} from 'react'
import anime from "animejs";
const [flag, setFlag] = useState(false);

function animation() {
  console.log("renderd");
  anime({
    targets: '#Point',
    scale: 4,
    duration: 5000,
    easing: 'linear',
    complete: function () {
      anime({
        targets: '#Point',
        scale: 1,
        duration: 1000,
        easing: 'linear'
      })
    }
  });
}

class Page2 extends Component {

  render(){
    setFlag(this.props.display);
    this.animation();
    return(
        <div style={{display: flag?'': 'none'}}>
          <div id="Point" style={{position: "absolute", left: "50%", top: "50%"}}/>
          <div className="text-center" style={{position:"absolute", left: "50%", marginTop: "-50px", marginLeft: "-50px", bottom: "13%", color: "red"}}> This is a point </div>
        </div>
    );

  }
}

export default Page2;
