import '../css/page4.css';
import React from 'react'
import anime from "animejs";

function Page4(props){
  function animation(){
    anime.timeline().add({
      targets: '#Page4Point',
      scale: 4,
      duration: 1000,
      easing: 'spring',
      delay: 1000
    }).add({
      targets: '#Page4Point',
      scaleX: [1,0],
      opacity: [1,0],
      easing: "easeOutExpo",
      offset: '-=875',
      delay: 1000
    }).add({
      targets: '#Page4Text1',
      scaleX: [1,0],
      opacity: [1,0],
      easing: "easeOutExpo",
      offset: '-=875',
      delay: 1000
    });
  }
    if(props.display) animation();

    return(
        <div style={{display: props.display?'': 'none'}}>
          <div id="Page4Point"> X </div>
          <div id="Page4Text1"> Now the node stores value <span id="White"> X </span></div>
        </div>
    );
}

export default Page4;
