import './App.css';
import React, {useEffect, useRef, useState} from "react";
import Page0 from "./pages/page0";
import Page2 from "./pages/page2";
import anime from "animejs";
import Page1 from "./pages/page1";



function App() {
  const [count, setCount] = useState(0);
  function clickBack(){
    setCount(count-1);
  }

  function clickNext(){
    setCount(count+1);
  }

  function animation(){
    let animation1 = anime.timeline({loop: true})
    .add({
      targets: '#Text',
      scale: [0.3,1],
      opacity: [0,1],
      translateZ: 0,
      easing: "easeOutExpo",
      duration: 600,
      delay: (el, i) => 70 * (i+1)
    }).add({
      targets: '#Text',
      scaleX: [0,1],
      opacity: [0.5,1],
      easing: "easeOutExpo",
      duration: 700,
      offset: '-=875',
      delay: (el, i, l) => 80 * (l - i)
    }).add({
      targets: '#Text',
      opacity: 0,
      duration: 1000,
      easing: "easeOutExpo",
      delay: 1000
    });
  }


  return (

      <div className="App">
        <div className="content">
          click this {count}
        </div>
        <div id="Text"> hello world </div>
        <Page0 display = {count === 0}/>
        <Page1 display = {count === 1}/>
        <Page2 display = {count === 2}/>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
