import '../css/page2.css';
import React, {Component, useState} from 'react'
import anime from "animejs";

function Page2(props){
  const [flag, setFlag] = useState(false);
  function animation(){
    anime({
      targets: '#Point',
      scale: 4,
      duration: 5000,
      easing: 'linear',
      complete: function () {
        console.log("rerendering");
        setFlag(true);
        anime({
          targets: '#Point',
          scale: 1,
          duration: 1000,
          easing: 'linear'
        })
      }
    });
  }
    if(props.display) animation();
    return(
        <div style={{display: props.display?'': 'none'}}>
          <div id="Point" style={{position: "absolute", left: "50%", top: "50%"}}/>
          <div className="text-center" style={{position:"absolute", left: "50%", marginTop: "-50px", marginLeft: "-50px", bottom: "13%", display: flag?'': 'none'}}> This is a point </div>
        </div>
    );
}

export default Page2;
