import './App.css';
import React, {useState} from "react";
import Page0 from "./pages/page0";
import Page1 from "./pages/page1";
import Page2 from "./pages/page2";
import Page3 from "./pages/page3";



function App() {
  const [count, setCount] = useState(1);
  function clickBack(){
    setCount(count-1);
  }

  function clickNext(){
    setCount(count+1);
  }

  return (
      <div className="App">
        <div className="content" style={{display: count===0?'': 'none'}}>
          click this {count}
        </div>
        <Page0 display = {count === 0}/>
        <Page1 display = {count === 1}/>
        <Page2 display = {count === 2}/>
        <Page3 display = {count === 3}/>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack} style={{display: count>1?'': 'none'}}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
