import '../css/page20.css';
import React from 'react'
import anime from "animejs";

function Page20(props){

  function animation(){
// 获取圆形div和进度条元素
    let circle = document.getElementById('circle');
    let progress = document.getElementById('progress');

// 设置初始进度（0%）
    let initialProgress = 0;

// 设置目标进度（75%）
    let targetProgress = 0.45;

// 设置动画属性
    let animation = anime({
      targets: progress,
      strokeDasharray: [0, 2 * Math.PI * circle.offsetWidth],
      strokeDashoffset: function(el) {
        return anime.setDashoffset(el);
      },
      easing: 'linear',
      duration: 1000,
      delay: function(el, i) { return i * 100; },
      elasticity: 200,
      autoplay: false
    });

// 调节进度的函数
    function setProgress(value) {
      // 将进度值限制在0到1之间
      let progressValue = Math.min(Math.max(value, 0), 1);

      // 更新进度条动画
      progress.oninput = function() {animation.seek(animation.duration * progressValue) };

      // 更新进度文本显示
      console.log('当前进度：' + (progressValue * 100) + '%');
    }

// 示例：动态循环变化进度从0%到75%
    animation.play();
    setProgress(targetProgress);
  }
  if(props.display) animation();


  return(
      <div style={{display: props.display?'': 'none'}}>
        <div id="circle" className="circle">
          <div id="progress" className="progress"/>
        </div>
      </div>
  );
}

export default Page20;
