import './App.css';
import React, {useEffect, useRef, useState} from "react";
import Page1 from "./pages/page1";
import Page2 from "./pages/page2";
import anime from "animejs";

function animation(){
  console.log("renderd");
  anime({
    targets: '#Point',
    scale: 4,
    duration: 5000,
    easing: 'linear',
    complete: function () {
      anime({
        targets: '#Point',
        scale: 1,
        duration: 1000,
        easing: 'linear'
      })
    }
  });
}
React.memo(animation);

function App() {
  const [count, setCount] = useState(0);
  function clickBack(){
    setCount(count-1);
  }

  function clickNext(){
    setCount(count+1);
  }


  return (
      <div className="App">
        <div className="content">
          click this {count}
        </div>
        <Page1 display = {count === 1}/>
        <Page2 display = {count === 2}/>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
