import React, {useRef, useState} from "react";
import anime from "animejs";

function Page1(props){
  const [display1, setDisplay1] = useState(true);
  const [display2, setDisplay2] = useState(true);
  // const timeout1 = setTimeout(function () {
  //   setDisplay1(true);
  // }, 1);
  //
  // const timeout2 = setTimeout(function () {
  //   setDisplay2(true);
  // }, 1);

  function animation(){
    let animation1 = anime.timeline({loop: true})
    .add({
      targets: '.ml1 .letter',
      scale: [0.3,1],
      opacity: [0,1],
      translateZ: 0,
      easing: "easeOutExpo",
      duration: 600,
      delay: (el, i) => 70 * (i+1)
    }).add({
      targets: '.ml1 .line',
      scaleX: [0,1],
      opacity: [0.5,1],
      easing: "easeOutExpo",
      duration: 700,
      offset: '-=875',
      delay: (el, i, l) => 80 * (l - i)
    });
  }
  if(props.display) animation();
  return(
      <div style={{display: props.display?'': 'none'}}>
        <h1 id="Text" style={{display: display1?'': 'none'}}>
          Raft


        <div id="Text" style={{display: display2?'': 'none'}}>
          Understandable Distributed Consensus
        </div>
        </h1>
      </div>
  );
}

export default Page1;
