import './App.css';
import ReactAnime from 'react-animejs'
import React, {useState} from "react";
const {Anime, stagger} = ReactAnime;



function App() {
  const [count, setCount] = useState(0);
  const [showPoint, setShowPoint] = useState(true);
  const [control, setControl] = useState(null); //controller state

  const [meta, setMeta] = useState({
    //meta state of the player
    control: control,
    progress: 0,
    currentTime: 0,
    duration: 0
  });
  function clickBack(){
    setCount(count-1);
    setShowPoint(!showPoint);
  }

  function clickNext(){
    setCount(count+1);
    setShowPoint(!showPoint);
  }
  console.log(showPoint);
  return (

      <div className="App">

        <div className="content" style={{display: showPoint?'': 'none'}}>
          click this  {count}
        </div>
        <Anime className="animation" style={{display: showPoint?'': 'none'}}
           control={control}
               setMeta={setMeta} // important to pull state of the player
               animeConfig={{
                 autoplay: false,
                 duration: 1500,
                 easing: "easeInOutSine"
               }}
            initial={[
              {
                targets: "#Box1",
                keyframes: [
                  {
                    translateX: 50
                  },
                  {
                    translateY: 50
                  },
                  {
                    translateX: 0
                  },
                  {
                    translateY: 0
                  }
                ],
                // easing:'spring',
                duration: 3500,
                loop: true
              },
              {
                targets: "#triangle2",
                keyframes: [
                  {
                    translateX: 150
                  },
                  {
                    translateY: 150
                  },
                  {
                    translateX: 100
                  },
                  {
                    translateY: 100
                  }
                ],
                easing:'spring',
                duration: 5000,
                loop: true
              }
            ]}
        >
          {/*<div id="Box1" />*/}
          <div id="triangle2"/>
        </Anime>

        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
