import '../css/page200.css';
import React, { useEffect, useRef } from 'react';
import anime from "animejs";

function Page200(props) {
// 获取圆形div和进度条元素\
  const circleRef = useRef(null);
  const progressRef = useRef(null);
  useEffect(() => {
    const circle = circleRef.current;
    const progress = progressRef.current;

    // 设置初始进度（0%）
    let initialProgress = 0;

    // 设置目标进度（75%）
    const targetProgress = 0.95;

    // 设置动画属性
    const animation = anime({
      targets: progress,
      strokeDasharray: [0, 2 * Math.PI * circle.offsetWidth],
      strokeDashoffset: function (el) {
        return anime.setDashoffset(el);
      },
      easing: 'linear',
      duration: 2000,
      loop: true,
      autoplay: false
    });

    // 启动动画并设置进度
    animation.play();
    setProgress(targetProgress);

    // 调节进度的函数
    function setProgress(value) {
      // 将进度值限制在0到1之间
      const progressValue = Math.min(Math.max(value, 0), 1);

      // 更新进度条动画
      animation.seek(animation.duration * progressValue);

      // 更新进度文本显示
      console.log('当前进度：' + (progressValue * 100) + '%');
    }

    return () => {
      // 在组件卸载时停止动画
      animation.pause();
    };
  }, []);

  return (
      <div style={{display: props.display?'': 'none'}}>
        <div className="circle" ref={circleRef}>
          <svg className="progress" ref={progressRef}>
            <circle cx="50%" cy="50%" r="45%" fill="transparent" />
          </svg>
        </div>
      </div>
  );
}

export default Page200;
