import '../css/page31.css';
import React, {useEffect, useRef, useState} from 'react';
import anime from "animejs";

function Page31(props){

  let obj3 = document.querySelector('#Page31NodeTerm1');
  if(obj3 !== null) obj3.innerHTML = 'Term: 0';
  let obj4 = document.querySelector('#Page31NodeTerm2');
  if(obj4 !== null) obj4.innerHTML = 'Term: 0';

  let obj5 = document.querySelector('#Page31VoteCount1');
  if(obj5 !== null) obj5.innerHTML = 'Voted For: C';
  let obj6 = document.querySelector('#Page31VoteCount2');
  if(obj6 !== null) obj6.innerHTML = 'Voted For: D';

  anime({
    targets: '#Page31Request3',
    opacity: 0,
    translateX: -70,
    translateY: 125,
    easing: "linear",
    duration: 0,
  });

  anime({
    targets: '#Page31Request4',
    opacity: 0,
    translateX: 125,
    translateY: 125,
    easing: "linear",
    duration: 0,
  });

  anime({
    targets: '#Page31Request5',
    opacity: 0,
    translateX: -70,
    translateY: 125,
    easing: "linear",
    duration: 0,
  });

  anime({
    targets: '#Page31Request6',
    opacity: 0,
    translateX: 125,
    translateY: 125,
    easing: "linear",
    duration: 0,
  });

  const progressBarRefs = useRef([]);
  const animationsRef = useRef(null);

  useEffect(() => {
    const progressBars = progressBarRefs.current;

    const animations = progressBars.map((progressBar, index) =>
        anime({
          targets: progressBar,
          strokeDasharray: getStrokeDashArray(index), // 进度动画
          easing: 'linear',
          duration: 3000,
          loop: false,
          direction: "normal",
          autoplay: true,
          // complete: function() {
          //   let obj1 = document.querySelector('#Page31Node3');
          //   if(obj1 !== null) obj1.style.borderStyle = 'dotted';
          //   let obj2 = document.querySelector('#Page31Node4');
          //   if(obj2 !== null) obj2.style.borderStyle = 'dotted';
          //
          //   let obj3 = document.querySelector('#Page31NodeTerm3');
          //   if(obj3 !== null) obj3.innerHTML = 'Term: 1';
          //   let obj4 = document.querySelector('#Page31NodeTerm4');
          //   if(obj4 !== null) obj4.innerHTML = 'Term: 1';
          //
          //   let obj5 = document.querySelector('#Page31VoteCount3');
          //   if(obj5 !== null) obj5.innerHTML = 'Vote Count: 1';
          //   let obj6 = document.querySelector('#Page31VoteCount4');
          //   if(obj6 !== null) obj6.innerHTML = 'Vote Count: 1';
          // }
        })
    );

    animationsRef.current = animations;
    return () => {
      animations.forEach((animation) => animation.pause()); // 在组件卸载时停止动画
    };
  }, []);

  const getStrokeDashArray = (index) => {
    switch (index) {
      case 0:
        return ['220 251.2', '240 251.2'];
      case 1:
        return ['160 251.2', '200 251.2'];
      case 2:
        return ['1 251.2', '90 251.2'];
      case 3:
        return ['1 251.2', '50 251.2'];
      default:
        return [];
    }
  };

  function animation() {
    let animation1 = anime.timeline().add({
      targets: '#Page31Request1',
      opacity: 1,
      translateX: -70,
      translateY: 125,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page31Request1',
      translateX: 125,
      translateY: 125,
      easing: "linear",
      duration: 3000,
      complete: function () {
        anime({
          targets: '#Page31Request1',
          opacity: 0,
          translateX: -70,
          translateY: 125,
          easing: "linear",
          duration: 0,
        });
      }
    });

    let animation2 = anime.timeline().add({
      targets: '#Page31Request2',
      opacity: 1,
      translateX: 125,
      translateY: 125,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page31Request2',
      translateX: -70,
      translateY: 125,
      easing: "linear",
      duration: 3000,
      complete: function() {
        anime({
          targets: '#Page31Request2',
          opacity: 0,
          translateX: 125,
          translateY: 125,
          easing: "linear",
          duration: 0,
        });
      }
    });

    let animation3 = anime.timeline().add({
      targets: '#Page31Request3',
      opacity: 1,
      translateX: -70,
      translateY: 125,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page31Request3',
      translateX: 60,
      translateY: -8.4,
      easing: "linear",
      duration: 3000,
    });

    let animation4 = anime.timeline().add({
      targets: '#Page31Request4',
      opacity: 1,
      translateX: 125,
      translateY: 125,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page31Request4',
      translateX: -5,
      translateY: -8.4,
      easing: "linear",
      duration: 3000,
    });

    let animation5 = anime.timeline().add({
      targets: '#Page31Request5',
      opacity: 1,
      translateX: -70,
      translateY: 125,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page31Request5',
      translateX: -70,
      translateY: -30,
      easing: "linear",
      duration: 3000,
    });

    let animation6 = anime.timeline().add({
      targets: '#Page31Request6',
      opacity: 1,
      translateX: 125,
      translateY: 125,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page31Request6',
      translateX: 125,
      translateY: -30,
      easing: "linear",
      duration: 3000,
    });
  }

  if(props.display) animation();

  useEffect(() => {
    if (props.display) {
      animationsRef.current.forEach((animation) => {
        animation.restart();
      });
    }
  }, [props.display]);

  return(
