import './App.css';
import ReactAnime from 'react-animejs'
import React, {useEffect, useState} from "react";
import {useAnime} from "./useAnime";
const {Anime, stagger} = ReactAnime;



function App() {
  const [count, setCount] = useState(0);
  const [showPoint, setShowPoint] = useState(true);

  function clickBack(){
    setCount(count-1);
    setShowPoint(!showPoint);
  }

  function clickNext(){
    setCount(count+1);
    setShowPoint(!showPoint);
  }
  console.log(showPoint);

  const Animate = () => {
    const { animateTargetRef, animationRef } = useAnime({
      translateX: 300,
      loop: true,
      duration: 2000,
      autoplay: false,
    });

    useEffect(() => {
      setTimeout(() => {
        animationRef.current?.play?.();
      }, 3000);
    }, [animationRef]);

    return (
        <div
            ref={animateTargetRef}
            style={{ width: '100px', height: '100px', backgroundColor: 'black' }}
        />
    );
  };

  return (

      <div className="App">

        <div className="content" style={{display: showPoint?'': 'none'}}>
          click this  {count}
        </div>
        <Anime className="animation"
            initial={[
              {
                targets: "#Box1",
                keyframes: [
                  {
                    translateX: 50
                  },
                  {
                    translateY: 50
                  },
                  {
                    translateX: 0
                  },
                  {
                    translateY: 0
                  }
                ],
                // easing:'spring',
                duration: 3500,
                loop: true
              }
            ]}
        >
          {/*<div id="Box1" />*/}
        </Anime>

        <Anime className="animation"
              initial={[
                {
                  targets: "#Box2",
                  keyframes: [
                    {
                      translateX: 150
                    },
                    {
                      translateY: 150
                    },
                    {
                      translateX: 100
                    },
                    {
                      translateY: 100
                    }
                  ],
                  // easing:'spring',
                  duration: 5000,
                  loop: true
                }
              ]}
                >
          <div id="Box1" style={{display: showPoint?'': 'none'}}/>
          <div id="Box2" style={{display: showPoint?'': 'none'}}/>
        </Anime>
        Animate
        <div className="continueButton">
          <button type="button" className="btn btn-outline-danger" onClick={clickBack}> Back &lt;</button>
          <button type="button" className="btn btn-outline-danger" onClick={clickNext}> Next &gt;</button>
        </div>
      </div>
  );
}

export default App;
