import '../css/page38.css';
import React, {useEffect, useRef, useState} from 'react';
import anime from "animejs";

function Page38(props){
  let obj = document.querySelector('#Page38Log2');
  if(obj !== null) obj.style.display = 'none';

  const progressBarRefs = useRef([]);
  const animationsRef = useRef(null);
  anime.timeline().add({
    targets: '#Page38HeartBeat1',
    opacity: 1,
    translateX: 127.5,
    translateY: 28.5,
    easing: "linear",
    duration: 0,
  }).add({
    targets: '#Page38HeartBeat2',
    opacity: 1,
    translateX: 178.5,
    translateY: 129,
    easing: "linear",
    duration: 0,
  });

  useEffect(() => {
    const progressBars = progressBarRefs.current;

    const animations = progressBars.map((progressBar, index) =>
        anime.timeline({
          loop: false,
          autoplay: true,
        }).add({
          targets: progressBar,
          strokeDasharray: getStrokeDashArray(index), // 进度动画
          easing: 'linear',
          duration: 1500,
          direction: "normal",
        }).add({
          targets: progressBar,
          strokeDasharray: ['1 251.2', '1 251.2'], // 进度动画
          easing: 'linear',
          duration: 0,
          direction: "normal",
        }).add({
          targets: progressBar,
          strokeDasharray: getStrokeDashArray(index+3), // 进度动画
          easing: 'linear',
          duration: 2000,
          direction: "normal",
        })
    );

    animationsRef.current = animations;
    return () => {
      animations.forEach((animation) => animation.pause()); // 在组件卸载时停止动画
    };
  }, []);

  const getStrokeDashArray = (index) => {
    switch (index) {
      case 0:
        return ['65 251.2', '95 251.2'];
      case 1:
        return ['1 251.2', '65 251.2'];
      case 2:
        return ['65 251.2', '95 251.2'];
      case 3:
        return ['1 251.2', '50 251.2'];
      case 4:
        return ['1 251.2', '65 251.2'];
      case 5:
        return ['1 251.2', '50 251.2'];
      default:
        return [];
    }
  };

  function animation() {

    let animation1 = anime.timeline({
      loop: false,
    }).add({
        targets: '#Page38HeartBeat1',
        opacity: 1,
        translateX: 127.5,
        translateY: 28.5,
        easing: "linear",
        duration: 0,
      }).add({
        targets: '#Page38HeartBeat1',
        translateX: 178,
        translateY: -72,
        easing: "linear",
        duration: 1500,
      }).add({
        targets: '#Page38HeartBeatResponse1',
        opacity: 1,
        translateX: 178,
        translateY: -72,
        easing: "linear",
        duration: 0,
    }).add({
        targets: '#Page38HeartBeatResponse1',
        translateX: 77,
        translateY: 129,
        easing: "linear",
        duration: 1500,
      complete: function () {
        anime({
          targets: '#Page38HeartBeatResponse1',
          opacity: 1,
          translateX: 178,
          translateY: -72,
          easing: "linear",
          duration: 0,
        });
      }
    }).add({
      targets: '#Page38HeartBeat1',
      translateX: 102.25,
      translateY: 78.75,
      easing: "linear",
      duration: 500,
    });

    let animation2 = anime.timeline({
      loop: false,
    }).add({
      targets: '#Page38HeartBeat2',
      opacity: 1,
      translateX: 178.5,
      translateY: 129,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page38HeartBeat2',
      translateX: 280,
      easing: "linear",
      duration: 1500,
    }).add({
      targets: '#Page38HeartBeatResponse2',
      opacity: 1,
      translateX: 277,
      translateY: 129,
      easing: "linear",
      duration: 0,
    }).add({
      targets: '#Page38HeartBeatResponse2',
      translateX: 77,
      easing: "linear",
      duration: 1500,
      complete: function () {
        anime({
          targets: '#Page38HeartBeatResponse2',
          opacity: 1,
          translateX: 277,
          translateY: 129,
          easing: "linear",
          duration: 0,
        });
      }
    }).add({
      targets: '#Page38HeartBeat2',
      translateX: 127.75,
      easing: "linear",
      duration: 500,
    });
  }

  if(props.display) animation();

  useEffect(() => {
    if (props.display) {
      animation();
      animationsRef.current.forEach((animation) => {
        animation.restart();
      });
    }
  }, [props.display]);

  return(
      <div style={{display: props.display?'': 'none'}}>
        <div className="Page38NodeDes" id="Page38NodeDes1">
          <div className="NodeDesContent"> Node b</div>
          <div className="NodeDesContent" id="Page38NodeTerm1"> Term: 1</div>
          <div className="NodeDesContent" id="Page38VoteCount1"> Leader: A</div>
        </div>
        
        <table className="Page38Log" id="Page38Log1">
          <tbody>
          <tr id="TR">
            <th>
              Log
            </th>
          </tr>
          <tr id="TR">
            <td>
              <font id='Page38Log1Text'> SET 5 </font>
            </td>
          </tr>
          </tbody>
        </table>
        
        <div className="Page38Node" id="Page38Node1">
          <svg className="progress-bar" viewBox="0 0 100 100">
            <circle
                className="progress-bar-background"
                id="Page38ProgressBarBG1"
                cx="50"
