import '../css/page4.css';
import React from 'react'
import anime from "animejs";

function Page4(props){
  function animation(){
    anime.timeline().add({
      targets: '#Point',
      scale: 4,
      duration: 3000,
      easing: 'linear',
      complete: function () {
        console.log("rerendering");
        anime({
          targets: '#Point',
          scale: 1,
          duration: 1000,
          easing: 'linear'
        })
      }
    }).add({
      targets: '#Text1',
      scaleX: [0,1],
      opacity: [0,1],
      easing: "easeOutExpo",
      duration: 700,
      offset: '-=875',
      delay: (el, i, l) => 80 * (l - i)
    }).add({
      targets: '#Text2',
      scaleX: [0,1],
      opacity: [0,1],
      easing: "easeOutExpo",
      duration: 700,
      offset: '-=875',
      delay: (el, i, l) => 80 * (l - i)
    }).add({
      targets: '#Text3',
      scaleX: [0,1],
      opacity: [0,1],
      easing: "easeOutExpo",
      duration: 700,
      offset: '-=875',
      delay: (el, i, l) => 80 * (l - i)
    });
  }
    if(props.display) animation();

    return(
        <div style={{display: props.display?'': 'none'}}>
          <div id="Point"/>
          <div id="Text1"> This is a <span id="Blue">node</span> </div>
          <div id="Text2"> Let's say we have a single node system </div>
          <div id="Text3"> For example, you can think of the <span id="Blue">node</span> as a database server that stores a single value.</div>
        </div>
    );
}

export default Page4;
